import React, { Component } from 'react';
import "./UserList.css";
import axios from "axios";

class User extends Component {

    deleteUser = () => {
        const token = localStorage.getItem('token');
        const login = localStorage.getItem('login');
        const password = localStorage.getItem('password');
        const defaultOptions = {
            headers: {
                Authorization: `${token}`
            }
        };
        axios.get('http://localhost:5002/api/users/self', {params: {login: this.props.data.login, password: this.props.data.password}})
            .then ((response) => (console.log(response)));
        axios.patch('http://localhost:5002/api/users/:id', {deleted: true, id: this.props.data.id}, defaultOptions)
            .catch(() => console.log("error"))
    };

    render() {
        return (
                <div className="user-list">
                    <div className="user-item">{this.props.data.login}</div>
                    <div className="user-item">{this.props.data.email}</div>
                    <div className="user-item">{this.props.data.role}</div>
                    <button onClick={this.deleteUser}>Удалить</button>
                </div>
        );
    }
}

export default User;
