import {ORIGAMI_LIST_PULL} from "../constants/bodyConstants";

export const origamiListReducer = (listState = [], action) => {
    switch (action.type) {
        case ORIGAMI_LIST_PULL: {
            listState = action.payload;
            return listState;
            break;
        }
        case "ORIGAMI_FILTER": {
            const newOrigami = listState.origami.filter(origami => origami.category === action.category);
            listState = {origami: newOrigami}
        }
        default: {
            return listState;
        }
    }
    return listState;
};
