import React, { Component } from 'react';
import "./DropDownItem.css"
import { Link } from "react-router-dom";

class DropDownItem extends Component {

    state = {
      typesVisible: false
    };

    dropItems = (item) => {
        if (item.name === "Виды оригами") this.setState({typesVisible: !this.state.typesVisible});
    };

    render() {

        const items = this.props.items.map((item) =>
            <div>
                <Link className = "drop-down-menu-item" to = {'/' + item.path}><button onClick={() => this.dropItems(item)} className="button">{item.name}</button></Link>
                 {item.name === "Виды оригами" && this.state.typesVisible ?
                     <div className="drop-items-container">
                         <Link to={'/module'} className = "drop-down-menu-item"><button className="button">Модульное оригами</button></Link>
                         <Link className = "drop-down-menu-item"><button className="button">Простое оригами</button></Link>
                         <Link className = "drop-down-menu-item"><button className="button">Развертка</button></Link>
                         <Link className = "drop-down-menu-item"><button className="button">Мокрое оригами</button></Link>
                         <Link className = "drop-down-menu-item"><button className="button">Оригами из денег</button></Link>
                         <Link className = "drop-down-menu-item"><button className="button">Квиллинг</button></Link>
                     </div> : null
                 }
            </div>);

        return (
            <div className="drop-down-menu">
                {items}
            </div>
        );
    }
}


export default DropDownItem;
