import {RELOAD} from "../constants/reloadConstants";

export const reloadReducer = (reloadState = false, action) => {
    switch (action.type) {
        case RELOAD: {
            reloadState = !reloadState;
        }
        default: {
            return reloadState;
        }
    }
    return reloadState;
};
