import React, { Component } from 'react';
import "../../MainBody.css"
import axios from "axios";

class Origami extends Component {

    state = {
      title: localStorage.getItem('login'),
      text:  ""
    };

    textReader = (e) => {
        this.setState({text: e.target.value})
    };

    createComment = () => {
        console.log(this.props.origamiId);
        const token = localStorage.getItem('token');
        const defaultOptions = {
            headers: {
                Authorization: `${token}`
            }
        };
        axios.post(
            'http://localhost:5002/api/comment/create', {
                text: this.state.text, origamiId: this.props.origamiId
            }, defaultOptions
        ).then(
            (response)  => {
                alert("pisa");
            }).catch(
            () => {
                console.log("error");
            }
        )
    };

    render() {
        if (this.props.visible)
        {
            return (
                <div className="comment">
                    <div className="comment-content">
                        <h2>Никита</h2>
                        <span>Отличная статья!</span>
                    </div>
                    <div className="comment-adder">
                        <input onChange={this.textReader} placeholder="Комментарий" />
                        <button onClick={this.createComment}>Отправить</button>
                    </div>
                </div>
            );
        }
        else return null;
    }
}

export default Origami;
