import React, { Component } from 'react';
import "../../MainBody.css"
import axios from "axios";

class Origami extends Component {

    state = {
      title: localStorage.getItem('login'),
      text:  ""
    };

    textReader = () => {
        this.setState({text: e.target.value})
    };

    createComment = () => {
        const token = localStorage.getItem('token');
        const defaultOptions = {
            headers: {
                Authorization: `${token}`
            }
        };
        axios.post(
            'http://localhost:5002/api/comments/create', {

            }
        ).then(
            (response)  => {
                this.setState({Message: "Вход успешно выполнен"});
                this.props.loginCheck({login: this.state.login, token: response.data.token, password: this.state.password});
                console.log(response);
                alert(this.state.Message);
            }).catch(
            () => {
                this.setState({Message: "Вы не зарегистрированы"});
                alert(this.state.Message);
            }
        )
    };

    render() {
        if (this.props.visible)
        {
            return (
                <div className="comment">
                    <h2>Андрей Зайцев</h2>
                    <span className="comment-content">Топ за свои то бабки</span>
                    <div>
                        <input onChange={() => this.textReader()} placeholder="Комментарий" />
                    </div>
                </div>
            );
        }
        else return null;
    }
}

export default Origami;
