export const origamiListReducer = (listState = {origami: [], category: ""}, action) => {
    switch (action.type) {
        case ORIGAMI_LIST_PULL: {
            listState = {origami: action.payload};
            return listState;
            break;
        }
        case "ORIGAMI_FILTER": {

        }
