import {ORIGAMI_LIST_PULL} from "../constants/bodyConstants";

export const origamiListReducer = (listState = {origami: [], category: ""}, action) => {
    switch (action.type) {
        case ORIGAMI_LIST_PULL: {
            listState = {origami: action.payload, category: listState.category};
            return listState;
            break;
        }
        case "ORIGAMI_FILTER": {
            listState = {origami: action.payload, category: ""};
        }
        default: {
            return listState;
        }
    }
    return listState;
};
