import {ORIGAMI_LIST_PULL} from "../constants/bodyConstants";

export const origamiListReducer = (listState = {origami: [], filter: []}, action) => {
    switch (action.type) {
        case ORIGAMI_LIST_PULL: {
            listState = {origami: action.payload, filter: action.payload};
            return listState;
            break;
        }
        case "ORIGAMI_FILTER": {
            listState.filter = listState.origami.filter(origami => origami.category === action.category);
            console.log("hi", listState, action);
            return listState;
            break;
        }
        default: {
            return listState;
        }
    }
    return listState;
};
