import {LOGIN_FALSE, LOGIN_TRUE} from "../constants/isLoginConstants";

export const isLoginReducer = (loginState = {status: false, path: '/autorisation', name: "Авторизация"}, action) => {
    switch (action.type) {
        case LOGIN_TRUE: {
            loginState = {status: true, path: '/profile', name: "Профиль", login: action.login};
            localStorage.setItem('token', action.token);
            return loginState;
            break;
        }
        case LOGIN_FALSE: {
            loginState = {status: false, path: '/autorisation', name: "Авторизация"}
        }
        default: {
            return loginState;
        }
    }
    return loginState;
};
