import React, { Component } from 'react';
import "./UserList.css";
import axios from "axios";

class User extends Component {

    deleteUser = () => {
        const token = localStorage.getItem('token');
        const defaultOptions = {
            headers: {
                Authorization: `${token}`
            },

        };
        axios.patch('http://localhost:5002/api/users/:' + this.props.data.id , {deleted: true, id: this.props.data.id}, defaultOptions)
            .catch(() => console.log("error"))
    };

    render() {
        console.log(this.props.data);
        return (
                <div className="user-list">
                    <div className="user-item">{this.props.data.login}</div>
                    <div className="user-item">{this.props.data.email}</div>
                    <div className="user-item">{this.props.data.role}</div>
                    <button onClick={this.deleteUser}>Удалить</button>
                </div>
        );
    }
}

export default User;
