import React, { Component } from 'react';
import "./Sidebar.css";
import axios from "axios";

class Sidebar extends Component {
    state = {
      surveyPage: {}
    };
    render() {
        let items = this.props.item;

        if (this.props.name === "Опрос") {
            const token = localStorage.getItem('token');
            axios.post('http://localhost:5002/api/survey/getPageSurvey', {surveyType: 'MainPage'} ,{headers: {Authorization: `${token}`}})
                .then((response) => {

                    this.setState({surveyPage: response.data.survey});
                });
            console.log(this.state.surveyPage);
            const points = this.state.surveyPage.points.map((point) => <div><p>{point.name}</p> <input type = "checkbox"/></div>);
            items = points;
        }

        else {
            items = items.map((item) => <button className="sidebar-button">{item}</button>);
        }

        return (
            <div className="sidebar">
                <h3 className="sidebar-title">{this.props.name}</h3>
                {items}
            </div>
        );
    }
}

export default Sidebar;
