import React, { Component } from 'react';
import "./DropDownItem.css"
import { Link } from "react-router-dom";

class DropDownItem extends Component {

    state = {
      typesVisible: false
    };

    render() {

        const items = this.props.items.map((item) =>
            <div>
                 <Link className = "drop-down-menu-item" to = {'/' + item.path}>{item.name}</Link>
                 {item.name === "Виды оригами" ?
                     <div>
                         <p>Модульное оригами</p>
                         <p>Простое оригами</p>
                         <p>Развертка</p>
                         <p>Мокрое оригами</p>
                         <p>Оригами из денег</p>
                         <p>Квиллинг</p>
                     </div> : null
                 }
            </div>);

        return (
            <div className="drop-down-menu">
                {items}
            </div>
        );
    }
}


export default DropDownItem;
